tables.table_schema:
CREATE TABLE users (
  user_id INT PRIMARY KEY,
  user_name VARCHAR(50),
  timezone VARCHAR(50)
);

CREATE TABLE LightSensors (
  sensor_id SERIAL PRIMARY KEY,
  location VARCHAR NOT NULL,
  sensor_type VARCHAR NOT NULL,
  status BOOLEAN NOT NULL
);

CREATE TABLE Users (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE Preferences (
  id INT PRIMARY KEY,
  user_id INT,
  bright_level INT,
  schedule_id INT
);

CREATE TABLE Locations (
  id INT PRIMARY KEY,
  location_name VARCHAR(255),
  timezone VARCHAR(50)
);

CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    preferred_lighting_mode STRING,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE LightingSchedule (
    schedule_id INT PRIMARY KEY,
    user_id INT REFERENCES UserPreferences(user_id),
    day_of_week STRING,
    lighting_mode STRING
);

CREATE TABLE AutomationSettings (
    settings_id INT PRIMARY KEY,
    user_id INT REFERENCES UserPreferences(user_id),
    automation_mode STRING
);
