  farmer_name TEXT,
  total_acres INT,
  location TEXT
);

CREATE TABLE Farmers (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50) NOT NULL,
    location VARCHAR(50) NOT NULL
);

CREATE TABLE Crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    yield_per_acre DECIMAL(10,2) NOT NULL,
    farmer_id INT REFERENCES Farmers(farmer_id)
);

CREATE TABLE CropHarvests (
    harvest_id INT PRIMARY KEY,
    crop_id INT REFERENCES Crops(crop_id),
    harvest_date DATE NOT NULL,
    quantity_harvested DECIMAL(10,2) NOT NULL
);

