tables.table_schema:
CREATE TABLE livestock_management (
    id INT PRIMARY KEY,
    animal_name VARCHAR(50),
    health_status VARCHAR(50),
    breeding_information TEXT
);

CREATE TABLE LivestockHealthRecords (
    id INT PRIMARY KEY,
    animal_id INT,
    health_status TEXT,
    medication TEXT
);

CREATE TABLE BreedingInformation (
    id INT PRIMARY KEY,
    animal_id INT,
    breeding_status TEXT,
    breeding_date DATE
);

CREATE TABLE Farms (
    farm_id INT PRIMARY KEY,
    farm_name VARCHAR(50),
    farm_location VARCHAR(100),
    area_acres FLOAT
);

CREATE TABLE Farmers (
    farmer_id SERIAL PRIMARY KEY,
    farmer_name VARCHAR(50),
    farm_location VARCHAR(100),
    contact_number VARCHAR(15)
);

CREATE TABLE Farm (
    farm_id SERIAL PRIMARY KEY,
    farm_location VARCHAR(100),
    farm_size DECIMAL,
    farm_manager_id INT
);
