tables.table_schema:
CREATE TABLE WaterUsage (
  id SERIAL PRIMARY KEY,
  date DATE NOT NULL,
  time TIME NOT NULL,
  usage_volume FLOAT NOT NULL
);

CREATE TABLE User (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  address VARCHAR(255)
);

CREATE TABLE WaterConsumption (
    user_id INT PRIMARY KEY,
    consumption_amount DECIMAL,
    consumption_date DATE
);

CREATE TABLE WaterLeakDetection (
    detection_id INT PRIMARY KEY,
    detection_date TIMESTAMP,
    leak_location VARCHAR,
    leak_status BOOLEAN
);

CREATE TABLE WaterConservationEvents (
    event_id INT PRIMARY KEY,
    event_date TIMESTAMP,
    conservation_type VARCHAR,
    description TEXT
);

CREATE TABLE WaterConsumptionPatterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR,
    pattern_description TEXT,
    pattern_type VARCHAR
);

CREATE TABLE WaterConsumption (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    date DATE NOT NULL,
    consumption_volume FLOAT NOT NULL
);

CREATE TABLE UserInformation (
    user_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    address VARCHAR(100),
    email VARCHAR(50) NOT NULL
);

CREATE TABLE ConservationActions (
    action_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    date_taken DATE NOT NULL,
    action_description TEXT NOT NULL
);

CREATE TABLE WaterSources (
    source_id SERIAL PRIMARY KEY,
    source_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);
