CREATE TABLE purchase_orders (
    order_id SERIAL PRIMARY KEY,
    supplier_id INT REFERENCES suppliers(supplier_id),
    order_date DATE,
    order_amount DECIMAL(10, 2),
    delivery_date DATE
);

