tables.table_schema:
CREATE TABLE energy_consumption_data (
    timestamp TIMESTAMP,
    energy_consumed DECIMAL,
    cost DECIMAL
);

CREATE TABLE tariff_rates (
    time_of_day TIME,
    tariff_rate DECIMAL
);

CREATE TABLE EnergyConsumptionData (
   id INT PRIMARY KEY,
   date TIMESTAMP,
   energy_consumed DECIMAL(10,2),
   device_id INT
);

CREATE TABLE TariffRates (
   tariff_id INT PRIMARY KEY,
   date_start TIMESTAMP,
   date_end TIMESTAMP,
   rate DECIMAL(10,2)
);

CREATE TABLE CostSavings (
   saving_id INT PRIMARY KEY,
   energy_consumption_id INT,
   tariff_id INT,
   saving_amount DECIMAL(10,2)
);

CREATE TABLE EnergyConsumption ( 
    timestamp TIMESTAMP, 
    device_id INT, 
    energy_consumed FLOAT 
);

CREATE TABLE TariffRates ( 
    tariff_id INT, 
    rate FLOAT 
);

CREATE TABLE CostSavings ( 
    timestamp TIMESTAMP, 
    device_id INT, 
    energy_saved FLOAT, 
    cost_savings FLOAT 
);
