tables.table_schema:
CREATE TABLE energy_usage (
    id INT PRIMARY KEY,
    sensor_id INT,
    occupancy_status BOOLEAN,
    energy_consumed DECIMAL
);

CREATE TABLE energy_usage (
    id INT PRIMARY KEY,
    room_id INT,
    energy_consumed FLOAT,
    energy_timestamp TIMESTAMP
);

CREATE TABLE occupancy_logs (
    id INT PRIMARY KEY,
    room_id INT,
    occupancy_event VARCHAR,
    occupancy_timestamp TIMESTAMP
);

CREATE TABLE efficiency_metrics (
    id INT PRIMARY KEY,
    room_id INT,
    efficiency_score FLOAT,
    efficiency_timestamp TIMESTAMP
);
