tables.table_schema:
CREATE TABLE PowerSchedule (
  schedule_id INT PRIMARY KEY,
  zone_id INT,
  power_demand FLOAT,
  forecast_date DATE
);

CREATE TABLE Zone (
  zone_id INT PRIMARY KEY,
  zone_name VARCHAR(50),
  capacity FLOAT,
  demand_forecast FLOAT
);

CREATE TABLE PowerZones (
  zone_id INT PRIMARY KEY,
  zone_name VARCHAR(50),
  demand_forecast DECIMAL
);

CREATE TABLE PowerSchedules (
  schedule_id INT PRIMARY KEY,
  schedule_name VARCHAR(50),
  start_time TIMESTAMP,
  end_time TIMESTAMP
);

CREATE TABLE Zone (
    zone_id INT PRIMARY KEY,
    zone_name VARCHAR(50) NOT NULL
);

CREATE TABLE PowerSchedule (
    schedule_id INT PRIMARY KEY,
