tables.table_schema:
CREATE TABLE energy_monitoring (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    renewable_production FLOAT,
    energy_consumption FLOAT
);

CREATE TABLE production (
  production_id INT PRIMARY KEY,
  production_type VARCHAR(255),
  production_value FLOAT
);

CREATE TABLE consumption (
  consumption_id INT PRIMARY KEY,
  consumption_type VARCHAR(255),
  consumption_value FLOAT
);

CREATE TABLE Production (
    production_id INT PRIMARY KEY,
    production_source VARCHAR(50) NOT NULL,
    production_amount DECIMAL NOT NULL,
    production_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Consumption (
    consumption_id INT PRIMARY KEY,
    consumption_device VARCHAR(50) NOT NULL,
    consumption_amount DECIMAL NOT NULL,
    consumption_timestamp TIMESTAMP NOT NULL
);
