tables.table_schema:
CREATE TABLE energy_storage (
    id INT PRIMARY KEY,
    date DATE,
    energy_consumed FLOAT,
    energy_generated FLOAT
);

CREATE TABLE charging_strategies (
    id INT PRIMARY KEY,
    date DATE,
    charging_duration INT,
    charging_efficiency FLOAT
);

CREATE TABLE historical_data (
    timestamp TIMESTAMP PRIMARY KEY,
    storage_id INT,
    energy_level DECIMAL,
    temperature DECIMAL
);

CREATE TABLE optimization_strategies (
    strategy_id INT PRIMARY KEY,
    storage_id INT,
    strategy_description TEXT,
    implementation_date DATE
);

CREATE TABLE energy_storage_system (
    id SERIAL PRIMARY KEY,
    date DATE,
    charging_strategy TEXT,
    discharging_strategy TEXT
);

CREATE TABLE historical_data (
    id SERIAL PRIMARY KEY,
    date DATE,
    energy_level FLOAT,
    temperature FLOAT
);

CREATE TABLE charging_data (
    id SERIAL PRIMARY KEY,
    date DATE,
    charging_rate FLOAT
);

CREATE TABLE discharging_data (
    id SERIAL PRIMARY KEY,
    date DATE,
    discharging_rate FLOAT
);

CREATE TABLE BatteryInfo (
    battery_id UUID PRIMARY KEY,
    date_time TIMESTAMPTZ,
    energy_level FLOAT,
    temperature FLOAT
);
