tables.table_schema:
CREATE TABLE VideoMetadata (
    video_id INT PRIMARY KEY,
    footage_name VARCHAR(255),
    timestamp TIMESTAMP,
    duration INT
);

CREATE TABLE FacialRecognition (
    face_id INT PRIMARY KEY,
    video_id INT,
    face_data JSON,
    confidence_score FLOAT
);

CREATE TABLE SecuritySystem (
    system_id INT PRIMARY KEY,
    system_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE security_system_videos (
    video_id UUID PRIMARY KEY,
    video_url TEXT NOT NULL,
    video_length INT,
    facial_recognition_enabled BOOLEAN
);

CREATE TABLE video_capture_data (
    capture_id UUID PRIMARY KEY,
    video_id UUID REFERENCES security_system_videos(video_id),
    capture_time TIMESTAMP,
    captured_frame BYTEA
);

CREATE TABLE facial_recognition_results (
    result_id UUID PRIMARY KEY,
    capture_id UUID REFERENCES video_capture_data(capture_id),
    recognized_faces INT[],
    unrecognized_faces INT[]
);

CREATE TABLE video_analysis_metadata (
    metadata_id UUID PRIMARY KEY,
    video_id UUID REFERENCES security_system_videos(video_id),
    analysis_time TIMESTAMP,
    analysis_result TEXT
);
