tables.table_schema:
CREATE TABLE known_criminals (
     criminal_id INT PRIMARY KEY,
     name VARCHAR(50),
     alias VARCHAR(50),
     description TEXT
);

CREATE TABLE crimes (
 crime_id INT PRIMARY KEY,
 crime_name TEXT,
 crime_description TEXT,
 crime_date DATE
);

CREATE TABLE criminals_crimes (
 criminal_id INT,
 crime_id INT,
 FOREIGN KEY (criminal_id) REFERENCES criminals(criminal_id),
 FOREIGN KEY (crime_id) REFERENCES crimes(crime_id)
);

CREATE TABLE criminal_photos (
 photo_id INT PRIMARY KEY,
 criminal_id INT,
 photo_url TEXT,
 FOREIGN KEY (criminal_id) REFERENCES criminals(criminal_id)
);

CREATE TABLE aliases (
    alias_id INT PRIMARY KEY,
    alias_name VARCHAR,
    criminal_id INT,
    FOREIGN KEY (criminal_id) REFERENCES criminals(criminal_id)
);
