tables.table_schema:
CREATE TABLE security_personnel (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    location_point GEOMETRY,
    timestamp TIMESTAMP
);
CREATE TABLE surveillance_system (
    camera_id INT PRIMARY KEY,
    location_point GEOMETRY,
    coverage_area GEOMETRY,
    status VARCHAR(20)
);

CREATE TABLE security_personnel_locations (
    id INT PRIMARY KEY,
    personnel_name VARCHAR(50) NOT NULL,
    latitude DECIMAL(9,6) NOT NULL,
    longitude DECIMAL(9,6) NOT NULL
);

CREATE TABLE security_cameras (
    id INT PRIMARY KEY,
    camera_name VARCHAR(50) NOT NULL,
    location_id INT REFERENCES security_personnel_locations(id)
);

CREATE TABLE security_personnel (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    phone_number VARCHAR(20),
    gps_location VARCHAR(100)
);

CREATE TABLE check_ins (
    id INT PRIMARY KEY,
    personnel_id INT,
    check_in_time TIMESTAMP,
    FOREIGN KEY (personnel_id) REFERENCES security_personnel(id)
);

CREATE TABLE patrol_routes (
    id INT PRIMARY KEY,
    route_name VARCHAR(50),
    start_location VARCHAR(100),
    end_location VARCHAR(100)
);

CREATE TABLE incidents (
    id INT PRIMARY KEY,
    description TEXT,
    location VARCHAR(100),
    timestamp TIMESTAMP
);

CREATE TABLE Location (
    location_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE SurveillanceSystem (
    system_id INT PRIMARY KEY,
    system_name VARCHAR(50),
    deployment_date DATE
);

CREATE TABLE CoverageArea (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(50),
    location_id INT,
    coverage_status BOOLEAN
);

CREATE TABLE PatrolActivity (
    activity_id INT PRIMARY KEY,
    personnel_id INT,
    activity_date DATE,
    area_id INT
);
