tables.table_schema:
CREATE TABLE occupancy_data (
    room_id INT NOT NULL,
    occupancy_status VARCHAR(10) NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    PRIMARY KEY (room_id, timestamp)
);

CREATE TABLE occupancy_data (
  id INT PRIMARY KEY,
  room_id INT,
  occupancy_status BOOLEAN,
  occupancy_timestamp TIMESTAMP
);

CREATE TABLE schedule_data (
  room_id INT PRIMARY KEY,
  heating_schedule JSON,
  cooling_schedule JSON
);

CREATE TABLE Occupancy_Data (
  id INT PRIMARY KEY,
  room_number INT,
  occupancy_status BOOLEAN,
  occupancy_timestamp TIMESTAMP
);

CREATE TABLE Heating_Schedule (
  id INT PRIMARY KEY,
  room_number INT,
  heating_start_time TIMESTAMP,
  heating_end_time TIMESTAMP
);

CREATE TABLE Cooling_Schedule (
  id INT PRIMARY KEY,
  room_number INT,
  cooling_start_time TIMESTAMP,
  cooling_end_time TIMESTAMP
);

CREATE TABLE smart_home_occupancy (
  id INT PRIMARY KEY,
  room_name VARCHAR(50),
  occupancy_status INT,
  occupancy_timestamp TIMESTAMP
);

CREATE TABLE occupancy_logs (
  id INT PRIMARY KEY,
  room_name VARCHAR(50),
  occupancy_status INT,
  occupancy_timestamp TIMESTAMP
);

CREATE TABLE OccupancyData (
    timestamp TIMESTAMP,
    room_id INT,
    occupancy_status BOOLEAN
);

CREATE TABLE RoomDetails (
    room_id INT,
    room_name VARCHAR,
    room_area FLOAT,
    temperature_sensor_id INT
);

CREATE TABLE TemperatureData (
    timestamp TIMESTAMP,
    room_id INT,
    temperature FLOAT
);

CREATE TABLE HeatingSchedule (
    room_id INT,
    heating_start_time TIMESTAMP,
    heating_end_time TIMESTAMP
);

CREATE TABLE CoolingSchedule (
    room_id INT,
