tables.table_schema:
CREATE TABLE user_location_tracking (
    user_id UUID PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE user_locations (
    user_id UUID PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE fitness_routes (
    route_id UUID PRIMARY KEY,
    route_name VARCHAR,
    distance FLOAT
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL
);

CREATE TABLE users (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE routes (
  route_id UUID PRIMARY KEY,
  start_location_id UUID REFERENCES user_locations(location_id),
  end_location_id UUID REFERENCES user_locations(location_id),
  distance DECIMAL(10, 2) NOT NULL
);

CREATE TABLE route_suggestions (
  suggestion_id UUID PRIMARY KEY,
  route_id UUID REFERENCES routes(route_id),
  user_id UUID REFERENCES users(user_id),
  timestamp TIMESTAMP
);

CREATE TABLE location_details (
    location_id INT NOT NULL,
    location_name VARCHAR(255) NOT NULL,
    city VARCHAR(100),
    country VARCHAR(100) NOT NULL
);

CREATE TABLE user_preferences (
    user_id INT NOT NULL,
    preference_id INT NOT NULL,
    value VARCHAR(255) NOT NULL
);

CREATE TABLE user_actions (
    action_id INT NOT NULL,
    user_id INT NOT NULL,
    action_type VARCHAR(50) NOT NULL,
    action_timestamp TIMESTAMP
);
