tables.table_schema:
CREATE TABLE energy_consumption_data (
    timestamp TIMESTAMP,
    location VARCHAR,
    energy_consumed DECIMAL
);

CREATE TABLE energy_consumption_data (
    id INT PRIMARY KEY,
    city VARCHAR(50),
    date DATE,
    consumption DECIMAL
);

CREATE TABLE sensors (
    sensor_id INT PRIMARY KEY,
    location VARCHAR(100),
    type VARCHAR(50),
    status VARCHAR(20)
);

CREATE TABLE energy_consumption_data (
    city_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    energy_reading DECIMAL
);

CREATE TABLE city_details (
    city_id INT PRIMARY KEY,
    city_name VARCHAR(255),
    country VARCHAR(255)
);

CREATE TABLE user_data (
    user_id INT PRIMARY KEY,
    city_id INT,
    energy_usage DECIMAL,
    CONSTRAINT fk_city FOREIGN KEY (city_id) REFERENCES city_details(city_id)
);

CREATE TABLE EnergyConsumptionData (
    id INT PRIMARY KEY,
    city_name VARCHAR(50),
    timestamp TIMESTAMP,
    energy_usage DECIMAL(10,2)
);

CREATE TABLE EnergyProviders (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(100),
    city_name VARCHAR(50),
    energy_source VARCHAR(50)
);
