tables.table_schema:
CREATE TABLE Smart_Cities_Demographics (
    city_id INT PRIMARY KEY,
    city_name VARCHAR,
    population INT,
    median_income DECIMAL
);

CREATE TABLE city_demographics (
    city_id INT PRIMARY KEY,
    city_name VARCHAR(50),
    population INT,
    median_income DECIMAL(10, 2)
);

CREATE TABLE demographics (
    id INT PRIMARY KEY,
    city VARCHAR(255),
    population INT,
    median_age FLOAT
);

CREATE TABLE resources (
    resource_id INT PRIMARY KEY,
    city VARCHAR(255),
    resource_type VARCHAR(50),
    quantity INT
);

CREATE TABLE urban_planning (
    plan_id INT PRIMARY KEY,
    city VARCHAR(255),
    plan_details TEXT
);

CREATE TABLE city_demographics (
    city_id INT PRIMARY KEY,
    city_name VARCHAR(50),
    population INT,
    area_sq_km FLOAT
);

CREATE TABLE demographic_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);
