tables.table_schema:
CREATE TABLE cross_org_data_sharing (
    contract_id SERIAL PRIMARY KEY,
    organization_id INT NOT NULL,
    data JSONB NOT NULL 
);

CREATE TABLE Organization (
	org_id INT PRIMARY KEY,
	org_name VARCHAR(100) NOT NULL,
	org_type VARCHAR(50) NOT NULL,
	org_size INT
);

CREATE TABLE SmartContract (
	contract_id INT PRIMARY KEY,
	contract_name VARCHAR(100) NOT NULL,
	org_id INT,
	CONSTRAINT fk_organization FOREIGN KEY (org_id) REFERENCES Organization(org_id)
);

CREATE TABLE SmartContracts (
    id INT PRIMARY KEY,
    contract_name VARCHAR(255),
    organization_id INT,
    contract_details TEXT
);

CREATE TABLE Organizations (
    id INT PRIMARY KEY,
    organization_name VARCHAR(255),
    industry VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE DataSharing (
    id INT PRIMARY KEY,
    shared_data_id INT,
    organization_id INT,
    status VARCHAR(50)
);

CREATE TABLE Organization (
  org_id UUID PRIMARY KEY,
  org_name VARCHAR(255) NOT NULL,
  org_description TEXT
);

CREATE TABLE User (
  user_id UUID PRIMARY KEY,
  user_name VARCHAR(255) NOT NULL,
  email VARCHAR(255) UNIQUE,
  org_id UUID REFERENCES Organization(org_id)
);

CREATE TABLE SharePermission (
  permission_id UUID PRIMARY KEY,
  permission_type VARCHAR(50) NOT NULL,
  contract_id UUID REFERENCES Contract(contract_id),
  org_id UUID REFERENCES Organization(org_id)
);

CREATE TABLE organization (
    org_id INT PRIMARY KEY,
    org_name VARCHAR(100) NOT NULL
);

CREATE TABLE smart_contract (
    contract_id INT PRIMARY KEY,
    contract_name VARCHAR(100) NOT NULL,
    org_id INT REFERENCES organization(org_id)
);

CREATE TABLE data_shared (
    data_id INT PRIMARY KEY,
    data_name VARCHAR(100) NOT NULL,
    contract_id INT REFERENCES smart_contract(contract_id)
);

CREATE TABLE data_access (
    access_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    contract_id INT REFERENCES smart_contract(contract_id)
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    access_time TIMESTAMP NOT NULL,
    access_type VARCHAR(50) NOT NULL,
    access_id INT REFERENCES data_access(access_id)
);
