tables.table_schema:
CREATE TABLE insurance_claim (
  claim_id INT PRIMARY KEY,
  policy_id INT NOT NULL,
  claimant_name VARCHAR(50) NOT NULL,
  claim_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE insurance_claims (
    claim_id SERIAL PRIMARY KEY,
    policy_holder_name VARCHAR(100) NOT NULL,
    claim_amount DECIMAL NOT NULL,
    claim_date DATE NOT NULL
);

CREATE TABLE claim_approval (
    approval_id SERIAL PRIMARY KEY,
    claim_id INT REFERENCES insurance_claims(claim_id),
    approval_status VARCHAR(20) NOT NULL
);

CREATE TABLE claim_history (
    claim_id INT,
    claim_date TIMESTAMP,
    claim_status VARCHAR(20),
    updated_by VARCHAR(100)
);

CREATE TABLE claim_documents (
    document_id INT PRIMARY KEY,
    claim_id INT,
    document_type VARCHAR(50),
    document_url VARCHAR(255)
);

CREATE TABLE claim_approval (
    approval_id INT PRIMARY KEY,
    claim_id INT,
    approver_name VARCHAR(50),
    approval_date DATE
);
