tables.table_schema:
CREATE TABLE mining_activities (
    activity_id INT PRIMARY KEY,
    miner_id INT,
    activity_type VARCHAR(50),
    activity_date TIMESTAMP
);

CREATE TABLE miners (
    miner_id INT PRIMARY KEY,
    miner_name VARCHAR(100),
    mining_power FLOAT,
    mining_address VARCHAR(255)
);

CREATE TABLE miners (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    country VARCHAR(50) NOT NULL,
    mining_power DECIMAL(10, 2) NOT NULL
);

CREATE TABLE blocks (
    block_number INT PRIMARY KEY,
    prev_block_number INT,
    num_transactions INT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Mining_Companies (
    company_id INT PRIMARY KEY,
    company_name TEXT NOT NULL,
    location TEXT
);

CREATE TABLE Mining_Pools (
    pool_id INT PRIMARY KEY,
    pool_name TEXT NOT NULL,
    total_miners INT,
    pool_fee FLOAT
);

CREATE TABLE Mining_Rewards (
    reward_id INT PRIMARY KEY,
    reward_amount FLOAT,
    reward_date DATE
);
