tables.table_schema:
CREATE TABLE financial_applications (
  id SERIAL PRIMARY KEY,
  app_name VARCHAR(50),
  app_version VARCHAR(20),
  app_description TEXT
);

CREATE TABLE audit_trail (
  id INT PRIMARY KEY,
  user_id INT,
  action VARCHAR,
  timestamp TIMESTAMP
);

CREATE TABLE compliance_data (
  id INT PRIMARY KEY,
  user_id INT,
  rule_id INT,
  description TEXT
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR,
  email VARCHAR,
  role VARCHAR
);

CREATE TABLE roles (
  role_id INT PRIMARY KEY,
  role_name VARCHAR,
  description TEXT
);
