tables.table_schema:
CREATE TABLE smart_contracts (
    contract_id SERIAL PRIMARY KEY,
    contract_name VARCHAR(255) NOT NULL,
    contract_details TEXT,
    creation_date TIMESTAMP
);

CREATE TABLE contract_parameters (
    parameter_id SERIAL PRIMARY KEY,
    contract_id INT REFERENCES smart_contracts(contract_id) ON DELETE CASCADE,
    parameter_name VARCHAR(255) NOT NULL,
    parameter_value TEXT
);

CREATE TABLE insurance_platforms (
    platform_id UUID PRIMARY KEY,
    platform_name VARCHAR(255),
    platform_address VARCHAR(255),
    supported_contracts JSON
);

CREATE TABLE smart_contracts (
    contract_id INT PRIMARY KEY,
    contract_name VARCHAR(255),
    contract_address VARCHAR(255),
    contract_balance DECIMAL
);

CREATE TABLE insurance_platforms (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(255),
    platform_address VARCHAR(255)
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255),
    user_address VARCHAR(255),
    user_type ENUM('admin', 'participant')
);

CREATE TABLE Documents (
    document_id INT PRIMARY KEY,
    claim_id INT,
    document_type ENUM('photo', 'pdf', 'text'),
    document_content TEXT
);
