tables.table_schema:
CREATE TABLE savings_accounts (
  account_id INT PRIMARY KEY,
  account_name VARCHAR(255) NOT NULL,
  account_balance DECIMAL(10, 2) NOT NULL,
  account_type VARCHAR(50) NOT NULL
);

CREATE TABLE decentralized_savings_accounts (
    account_id INT PRIMARY KEY,
    account_name VARCHAR(255) NOT NULL,
    account_balance DECIMAL(10, 2) NOT NULL,
    account_type VARCHAR(50)
);

CREATE TABLE personalized_recommendations (
    recommendation_id INT PRIMARY KEY,
    account_id INT NOT NULL,
    recommendation_text TEXT,
    recommendation_date DATE
);

CREATE TABLE savings_account (
   account_id INT PRIMARY KEY,
   account_name VARCHAR(50) NOT NULL,
   account_balance DECIMAL(10, 2) NOT NULL,
   created_date TIMESTAMP NOT NULL
);

CREATE TABLE recommendations (
   recommendation_id INT PRIMARY KEY,
   account_id INT NOT NULL,
   recommendation_text TEXT NOT NULL,
   recommendation_date TIMESTAMP NOT NULL
);

CREATE TABLE savings_accounts (
  client_id INT PRIMARY KEY,
  account_number INT NOT NULL,
  balance DECIMAL(10, 2) NOT NULL
);

CREATE TABLE investment_accounts (
  account_id INT PRIMARY KEY,
  client_id INT REFERENCES savings_accounts(client_id),
  investment_type VARCHAR(50) NOT NULL,
  investment_amount DECIMAL(12, 2) NOT NULL
);

CREATE TABLE recommendations (
  recommendation_id INT PRIMARY KEY,
  client_id INT REFERENCES savings_accounts(client_id),
  recommendation_text TEXT NOT NULL,
  recommendation_date DATE NOT NULL
);

CREATE TABLE financial_goals (
  goal_id INT PRIMARY KEY,
  client_id INT REFERENCES savings_accounts(client_id),
  goal_description TEXT NOT NULL,
  goal_target DECIMAL(10, 2) NOT NULL
);
