tables.table_schema:
CREATE TABLE warehouse_layout (
    warehouse_id UUID PRIMARY KEY,
    rack_id INT,
    sku_id INT,
    demand_rate FLOAT
);

CREATE TABLE WarehouseLayout (
    warehouse_id UUID PRIMARY KEY,
    warehouse_name STRING,
    warehouse_location STRING
);

CREATE TABLE SKUCharacteristics (
    sku_id UUID PRIMARY KEY,
    sku_name STRING,
    sku_description STRING
);

CREATE TABLE DemandPatterns (
    demand_id UUID PRIMARY KEY,
    sku_id UUID,
    demand_date DATE,
    demand_quantity INT
);

CREATE TABLE warehouse_layout (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    area_code VARCHAR(10),
    capacity INT
);
