tables.table_schema:
CREATE TABLE goods_tracking (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    location VARCHAR(255),
    movement_status CHAR(1)
);

CREATE TABLE goods (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255),
    rfid_tag VARCHAR(50) UNIQUE
);

CREATE TABLE locations (
    id SERIAL PRIMARY KEY,
    location_name VARCHAR(255),
    address TEXT
);
