CREATE TABLE InventoryRecords (
    record_id INT PRIMARY KEY,
    product_id INT,
    quantity INT,
    location VARCHAR(50)
);

CREATE TABLE Discrepancies (
    discrepancy_id INT PRIMARY KEY,
    record_id INT,
    description TEXT,
    resolved BOOLEAN
);

CREATE TABLE inventory_records (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    initial_stock INT,
    current_stock INT
);

CREATE TABLE discrepancy_logs (
    log_id INT PRIMARY KEY,
    product_id INT,
    discrepancy_date DATE,
    discrepancy_details TEXT
);

CREATE TABLE resolved_discrepancies (
    resolution_id INT PRIMARY KEY,
    log_id INT,
    resolution_date DATE,
    resolution_details TEXT
);

CREATE TABLE inventory_records (
    record_id INT PRIMARY KEY,
    product_name TEXT,
    quantity INT,
    last_updated TIMESTAMP
);

CREATE TABLE discrepancies (
    discrepancy_id INT PRIMARY KEY,
    record_id INT,
    discrepancy_reason TEXT,
    resolved BOOLEAN
);

CREATE TABLE resolved_discrepancies (
    resolved_id INT PRIMARY KEY,
    discrepancy_id INT,
    resolution_date DATE,
    resolved_by TEXT
);

CREATE TABLE stock_control (
    product_id INT PRIMARY KEY,
    current_stock INT,
    minimum_stock INT,
    maximum_stock INT
);

CREATE TABLE LocationMapping (
    location_id INT NOT NULL,
    location_name VARCHAR(50) NOT NULL
);

CREATE TABLE Discrepancies (
    discrepancy_id INT NOT NULL,
    record_id INT NOT NULL,
    discrepancy_description TEXT,
    resolution_status VARCHAR(20) NOT NULL
);

CREATE TABLE InventoryAuditTrail (
    audit_id INT NOT NULL,
    record_id INT NOT NULL,
    action_taken VARCHAR(100) NOT NULL,
    audit_timestamp TIMESTAMP NOT NULL
);
