tables.table_schema:
CREATE TABLE nft_owners (
    nft_owner_id INT PRIMARY KEY,
    nft_owner_name VARCHAR(255)
);

CREATE TABLE nfts (
    nft_id INT PRIMARY KEY,
    nft_name VARCHAR(255),
    nft_owner_id INT,
    FOREIGN KEY (nft_owner_id) REFERENCES nft_owners(nft_owner_id)
);

CREATE TABLE nfts (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  owner_id INT
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50)
);

CREATE TABLE user_nfts (
  user_id INT,
  nft_id INT,
  FOREIGN KEY (user_id) REFERENCES users(user_id),
  FOREIGN KEY (nft_id) REFERENCES nfts(id)
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL
);

CREATE TABLE nfts (
    nft_id UUID PRIMARY KEY,
    nft_name VARCHAR(100) NOT NULL,
    owner_id UUID NOT NULL,
    CONSTRAINT fk_owner FOREIGN KEY (owner_id) REFERENCES users(user_id)
);

CREATE TABLE user_sessions (
    session_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    login_time TIMESTAMP NOT NULL,
    CONSTRAINT fk_session_user FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL
);

CREATE TABLE nfts (
    nft_id UUID PRIMARY KEY,
    nft_name VARCHAR(100) NOT NULL,
    nft_description TEXT
);

CREATE TABLE user_nfts (
    user_id UUID REFERENCES users(user_id),
    nft_id UUID REFERENCES nfts(nft_id),
    PRIMARY KEY (user_id, nft_id)
);

CREATE TABLE users_balances (
    user_id UUID REFERENCES users(user_id),
    balance DECIMAL(18, 2) DEFAULT 0,
    PRIMARY KEY (user_id)
);
