tables.table_schema:
CREATE TABLE nft_table (
    id INT PRIMARY KEY,
    nft_name VARCHAR(255),
    owner_address VARCHAR(255),
    is_sold BOOLEAN
);

CREATE TABLE NFTTransactions (
    transaction_id INT PRIMARY KEY,
    nft_id INT REFERENCES NFTs(id),
    buyer VARCHAR(255),
    date_sold DATE
);

CREATE TABLE NFTs (
    id UUID PRIMARY KEY,
    name STRING,
    description STRING,
    owner_id UUID
);

CREATE TABLE Ownerships (
    ownership_id UUID PRIMARY KEY,
    nft_id UUID,
    owner_id UUID,
    transfer_time TIMESTAMP
);

CREATE TABLE NFTs (
    id INT PRIMARY KEY,
    nft_name VARCHAR(255),
    owner_id INT,
    is_sold BOOLEAN
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255),
    address VARCHAR(255)
);

CREATE TABLE Ownership (
    ownership_id INT PRIMARY KEY,
    nft_id INT,
    owner_id INT,
    is_current_owner BOOLEAN
);
