tables.table_schema:
CREATE TABLE NFTs (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    category VARCHAR(50),
    owner VARCHAR(255)
);

CREATE TABLE Categories (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    description VARCHAR(255)
);

CREATE TABLE nft_categories (
    category_id INT PRIMARY KEY,
    category_name TEXT
);

CREATE TABLE nft_items (
    item_id INT PRIMARY KEY,
    item_name TEXT,
    category_id INT REFERENCES nft_categories(category_id)
);

CREATE TABLE nft_owners (
    owner_id INT PRIMARY KEY,
    owner_name TEXT
);

CREATE TABLE nft_ownership (
    ownership_id INT PRIMARY KEY,
    item_id INT REFERENCES nft_items(item_id),
    owner_id INT REFERENCES nft_owners(owner_id)
);
