tables.table_schema:
CREATE TABLE nfts (
    id SERIAL PRIMARY KEY,
    nft_id INT,
    nft_name VARCHAR(255),
    owner_id INT
);
CREATE TABLE transactions (
    id SERIAL PRIMARY KEY,
    transaction_id INT,
    nft_id INT,
    transaction_date TIMESTAMP
);

CREATE TABLE nfts (
    nft_id UUID,
    nft_name VARCHAR,
    nft_owner UUID
);

CREATE TABLE transactions (
    transaction_id UUID,
    nft_id UUID,
    transaction_type VARCHAR
);

CREATE TABLE nft_history (
    history_id UUID,
    nft_id UUID,
    transaction_id UUID,
    history_date TIMESTAMP
);

CREATE TABLE users (user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100));

CREATE TABLE NFTs (
  id INT PRIMARY KEY,
  token_id VARCHAR(50) NOT NULL,
  owner_address VARCHAR(100) NOT NULL
);

CREATE TABLE Transactions (
  id INT PRIMARY KEY,
  transaction_id VARCHAR(50) NOT NULL,
  nft_id INT REFERENCES NFTs(id),
  transaction_type VARCHAR(50) NOT NULL
);

CREATE TABLE TransactionDetails (
  id INT PRIMARY KEY,
  transaction_id INT REFERENCES Transactions(id),
  timestamp TIMESTAMP,
  details JSONB
);

CREATE TABLE NFTMetadata (
  id INT PRIMARY KEY,
  nft_id INT REFERENCES NFTs(id),
  metadata JSONB
);

CREATE TABLE NFTImages (
  id INT PRIMARY KEY,
  nft_id INT REFERENCES NFTs(id),
  image_url VARCHAR(200) NOT NULL
);
