tables.table_schema:
CREATE TABLE nft_marketplace_prices (
    nft_id INT,
    nft_name VARCHAR(255),
    price DECIMAL(12, 2),
    purchase_date TIMESTAMP
);

CREATE TABLE NFTs (
    id INT PRIMARY KEY,
    nft_name VARCHAR(255),
    nft_price DECIMAL(10, 2),
    creator VARCHAR(255)
);

CREATE TABLE nft_marketplace_prices (
    id INT PRIMARY KEY,
    nft_name VARCHAR(255),
    price DECIMAL(10, 2),
    transaction_date DATE
);

CREATE TABLE nft_owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(255),
    nft_id INT,
    ownership_start_date DATE
);

CREATE TABLE NFT_Collection (
    nft_id INT PRIMARY KEY,
    nft_name VARCHAR(255),
    artist_name VARCHAR(255),
    release_date DATE
);

CREATE TABLE NFT_Ownership (
    ownership_id INT PRIMARY KEY,
    nft_id INT,
    owner_id INT,
    purchase_date DATE
);

CREATE TABLE NFT_Price_History (
    price_history_id INT PRIMARY KEY,
    nft_id INT,
    price DECIMAL(10, 2),
    transaction_date DATE
);

CREATE TABLE NFT_Tags (
    tag_id INT PRIMARY KEY,
    nft_id INT,
    tag_name VARCHAR(50)
);

CREATE TABLE NFTCollection (
    collection_id INT PRIMARY KEY,
    collection_name VARCHAR(255),
    total_items INT,
    created_on DATETIME
);

CREATE TABLE NFTUser (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255),
    joined_on DATE
);
