tables.table_schema:
CREATE TABLE NFTs (
    id SERIAL PRIMARY KEY,
    token_id STRING NOT NULL,
    owner STRING NOT NULL,
    encrypted_content BYTES NOT NULL
);

CREATE TABLE NFT_Encryption_Keys (
    id SERIAL PRIMARY KEY,
    token_id STRING NOT NULL,
    encryption_key BYTES NOT NULL
);

CREATE TABLE nft_users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE nft_items (
    item_id UUID PRIMARY KEY,
    item_name VARCHAR(100) NOT NULL,
    owner_id UUID NOT NULL,
    encrypted_data BYTES
);

CREATE TABLE NFT_Owners (
  id INT PRIMARY KEY,
  owner_name VARCHAR(50) NOT NULL,
  nft_id INT NOT NULL,
  encrypted_data BYTEA
);

CREATE TABLE NFTMetadata (
    id SERIAL PRIMARY KEY,
    item_id INT,
    metadata_key VARCHAR(50),
    metadata_value TEXT
);

CREATE TABLE NFTEncryptionKeys (
    id SERIAL PRIMARY KEY,
    user_id INT,
    encryption_key BYTEA
);
