tables.table_schema:
CREATE TABLE consensus_mechanism (
  id INT PRIMARY KEY,
  node_id UUID,
  round_number INT,
  is_leader BOOLEAN
);

CREATE TABLE transaction_validation (
  id SERIAL PRIMARY KEY,
  transaction_id UUID,
  signature TEXT,
  is_valid BOOLEAN
);

CREATE TABLE consensus_mechanism (
    id INT PRIMARY KEY,
    algorithm VARCHAR(50) NOT NULL,
    block_size INT NOT NULL
);

CREATE TABLE validation (
    id INT PRIMARY KEY,
    validator_address VARCHAR(50) NOT NULL,
    validated_tx_id INT NOT NULL,
    validation_result BOOLEAN NOT NULL
);

CREATE TABLE consensus_mechanism (
    id INT PRIMARY KEY,
    node_id INT,
    node_address VARCHAR,
    consensus_algorithm VARCHAR
);

CREATE TABLE block_chain (
    block_id INT PRIMARY KEY,
    previous_block_id INT,
    block_hash VARCHAR,
    timestamp TIMESTAMP
);

CREATE TABLE validation_log (
    log_id INT PRIMARY KEY,
    block_id INT,
    validator_node_id INT,
    validation_result VARCHAR
);

CREATE TABLE ConsensusMechanism (
   id SERIAL PRIMARY KEY,
   mechanism_name VARCHAR(100) NOT NULL,
   mechanism_type VARCHAR(50) NOT NULL
);

CREATE TABLE Validators (
   id SERIAL PRIMARY KEY,
   validator_name VARCHAR(100) NOT NULL,
   public_key VARCHAR(255) NOT NULL,
   validation_power INT NOT NULL
);

CREATE TABLE Blocks (
   id SERIAL PRIMARY KEY,
   block_hash VARCHAR(255) NOT NULL,
   block_number INT NOT NULL,
   timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Rewards (
   id SERIAL PRIMARY KEY,
   reward_amount DECIMAL(18, 2) NOT NULL,
   receiver_address VARCHAR(100) NOT NULL
);
