tables.table_schema:
CREATE TABLE blockchain_records (
    record_id SERIAL PRIMARY KEY,
    transaction_id INT NOT NULL,
    block_hash TEXT NOT NULL,
    block_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE prescription_drugs (
	drug_id INTEGER PRIMARY KEY,
	drug_name VARCHAR(255) NOT NULL,
	drug_cost DECIMAL(10, 2) NOT NULL
);

CREATE TABLE drug_transactions (
	transaction_id SERIAL PRIMARY KEY,
	drug_id INTEGER REFERENCES prescription_drugs(drug_id),
	transaction_date DATE NOT NULL,
	transaction_details TEXT
);

CREATE TABLE blockchain_transactions (
	block_id SERIAL PRIMARY KEY,
	transaction_id INTEGER REFERENCES drug_transactions(transaction_id),
	block_hash VARCHAR(64) NOT NULL,
	block_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE users (
	user_id SERIAL PRIMARY KEY,
	user_name VARCHAR(255) NOT NULL,
	user_email VARCHAR(255) UNIQUE
);

CREATE TABLE blockchain_data (
  block_id INT PRIMARY KEY,
  transaction_id INT,
  hash VARCHAR(100),
  previous_block_hash VARCHAR(100)
);

CREATE TABLE pharmacies (
  pharmacy_id INT PRIMARY KEY,
  pharmacy_name VARCHAR(50),
  location VARCHAR(100)
);

CREATE TABLE patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(50),
  address VARCHAR(100),
  contact_number VARCHAR(20)
);
