tables.table_schema:
CREATE TABLE patient_consent_forms (
    patient_id INT PRIMARY KEY,
    consent_form_hash BYTEA,
    consent_date TIMESTAMP
);

CREATE TABLE blockchain_records (
    record_id UUID PRIMARY KEY,
    patient_id INT REFERENCES patient_consent_forms(patient_id),
    transaction_id UUID
);

CREATE TABLE patients (
    patient_id UUID PRIMARY KEY,
    patient_name TEXT,
    patient_dob DATE,
    patient_address TEXT
);

CREATE TABLE patient_consent_forms (
    patient_id INT PRIMARY KEY,
    consent_date DATE,
    consent_text TEXT
);

CREATE TABLE blockchain_network (
    node_id INT PRIMARY KEY,
    node_address TEXT,
    node_status BOOLEAN
);

CREATE TABLE patient_records (
    record_id INT PRIMARY KEY,
    patient_id INT,
    record_data TEXT,
    FOREIGN KEY (patient_id) REFERENCES patient_consent_forms(patient_id)
);

CREATE TABLE patient_consents (
   patient_id UUID PRIMARY KEY,
   consent_text TEXT,
   consent_date TIMESTAMP
);

CREATE TABLE blockchain_network (
   network_id UUID PRIMARY KEY,
   network_name TEXT,
   network_url TEXT
);

CREATE TABLE audit_trail (
   audit_id UUID PRIMARY KEY,
   record_id UUID,
   action TEXT,
   action_date TIMESTAMP
);

CREATE TABLE users (
   user_id UUID PRIMARY KEY,
   username TEXT,
   password TEXT,
   role TEXT
);
