tables.table_schema:
CREATE TABLE insurance_contracts (
    contract_id UUID PRIMARY KEY,
    contract_holder VARCHAR(255) NOT NULL,
    insurance_type VARCHAR(100) NOT NULL,
    contract_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE claim_records (
    claim_id UUID PRIMARY KEY,
    contract_id UUID NOT NULL,
    claim_date DATE NOT NULL,
    claim_amount DECIMAL(10, 2) NOT NULL,
    status VARCHAR(50) NOT NULL
);

CREATE TABLE insurance_contracts (
    contract_id UUID PRIMARY KEY,
    smart_contract_addr STRING,
    policy_holder_id UUID,
    claim_amount DECIMAL
);

CREATE TABLE policy_holders (
    policy_holder_id UUID PRIMARY KEY,
    first_name STRING,
    last_name STRING,
    email STRING
);

CREATE TABLE claims (
    claim_id UUID PRIMARY KEY,
    contract_id UUID,
    claim_status STRING,
    claim_date TIMESTAMP
);

CREATE TABLE claimants (
    claimant_id UUID PRIMARY KEY,
    first_name STRING,
    last_name STRING,
    email STRING
);

CREATE TABLE contracts (
    contract_id UUID PRIMARY KEY,
    contract_name STRING,
    contract_type STRING,
    contract_terms TEXT
);

CREATE TABLE blockchain_transactions (
    tx_id UUID PRIMARY KEY,
    block_id UUID,
    transaction_type STRING,
    transaction_data JSONB
);
