tables.table_schema:
CREATE TABLE research_data (
    research_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    research_topic VARCHAR(100),
    research_data TEXT
);

CREATE TABLE blockchain_ledger (
    block_id INT PRIMARY KEY,
    data_hash VARCHAR(64),
    timestamp TIMESTAMP
);

CREATE TABLE researcher_profiles (
    researcher_id UUID PRIMARY KEY,
    name TEXT,
    email TEXT,
    affiliation TEXT
);

CREATE TABLE medical_research_data (
    research_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    research_topic VARCHAR(100) NOT NULL,
    research_data TEXT NOT NULL
);

CREATE TABLE authorized_researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    organization VARCHAR(100) NOT NULL
);

CREATE TABLE blockchain_ledger (
    block_id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    data_hash VARCHAR(256) NOT NULL
);

CREATE TABLE data_access_logs (
    access_id INT PRIMARY KEY,
    researcher_id INT NOT NULL,
    research_id INT NOT NULL,
    access_timestamp TIMESTAMP NOT NULL
);
