tables.table_schema:
CREATE TABLE property_ownership (
	property_id INT PRIMARY KEY,
	owner_id INT NOT NULL,
	owner_name VARCHAR(50) NOT NULL,
	ownership_date TIMESTAMP NOT NULL
);

CREATE TABLE property_ownership (
    property_id INT PRIMARY KEY,
    owner_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE property_ownership (
    id SERIAL PRIMARY KEY,
    property_id INT NOT NULL,
    owner_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE owner (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    address VARCHAR(100) NOT NULL
);

CREATE TABLE Property (
    property_id UUID PRIMARY KEY,
    property_name VARCHAR NOT NULL,
    property_value DECIMAL NOT NULL
);

CREATE TABLE Owner (
    owner_id UUID PRIMARY KEY,
    owner_name VARCHAR NOT NULL,
    owner_address VARCHAR NOT NULL
);

CREATE TABLE BlockchainNetwork (
    network_id UUID PRIMARY KEY,
    network_name VARCHAR NOT NULL,
    network_location VARCHAR NOT NULL
);

CREATE TABLE property_owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(255) NOT NULL
);

CREATE TABLE properties (
    property_id INT PRIMARY KEY,
    property_address VARCHAR(255) NOT NULL,
    owner_id INT,
    FOREIGN KEY (owner_id) REFERENCES property_owners(owner_id)
);
