tables.table_schema:
CREATE TABLE InterChainTransactionLogs (
    log_id SERIAL PRIMARY KEY,
    transaction_id INT REFERENCES InterChainTransactions(transaction_id),
    log_details TEXT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE AtomicSwapLogs (
    log_id SERIAL PRIMARY KEY,
    swap_id INT REFERENCES AtomicSwaps(swap_id),
    log_details TEXT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);
