tables.table_schema:
CREATE TABLE consensus_algorithm(
    id UUID PRIMARY KEY,
    node_id INT NOT NULL,
    block_id INT NOT NULL,
    consensus_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE blockchain_replication(
    replica_id INT PRIMARY KEY,
    node_id INT NOT NULL,
    block_id INT NOT NULL,
    replica_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE consensus_algorithm (
    id INT PRIMARY KEY,
    algorithm_name VARCHAR(50) NOT NULL,
    replication_factor INT NOT NULL
);

CREATE TABLE scalability (
    id INT PRIMARY KEY,
    block_id INT NOT NULL,
    shard_id INT NOT NULL,
    block_size INT
);

CREATE TABLE consensus_algorithm (
    id INT PRIMARY KEY,
    block_number INT,
    block_hash VARCHAR(255),
    consensus_status VARCHAR(50)
);

CREATE TABLE replication_log (
    log_id INT PRIMARY KEY,
    transaction_id INT,
    node_id INT,
    replication_status VARCHAR(50)
);

CREATE TABLE blockchain_node (
    node_id INT PRIMARY KEY,
    node_name VARCHAR(50),
    node_status VARCHAR(50)
);

CREATE TABLE scalability_metrics (
    metric_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    transaction_count INT,
    block_size INT
);

CREATE TABLE consensus_algorithm (
    id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    replication_factor INT
);

CREATE TABLE block_transactions (
    block_id INT,
    transaction_id INT,
    transaction_data TEXT,
    PRIMARY KEY (block_id, transaction_id)
);

CREATE TABLE validators (
    validator_id INT PRIMARY KEY,
    public_key VARCHAR(255),
    total_votes INT
);

CREATE TABLE blockchain_blocks (
    block_id INT PRIMARY KEY,
    previous_block_id INT,
    block_data TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE node_status (
    node_id INT PRIMARY KEY,
    is_active BOOLEAN,
    last_updated TIMESTAMP
);
