tables.table_schema:
CREATE TABLE permissions (
    participant_id INT PRIMARY KEY,
    access_level VARCHAR(50) NOT NULL
);

CREATE TABLE blockchain_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(50) NOT NULL,
    access_level VARCHAR(10) NOT NULL
);

CREATE TABLE blockchain_network_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(50) NOT NULL,
    access_level VARCHAR(20) NOT NULL
);

CREATE TABLE access_permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE participant_permissions (
    participant_id INT REFERENCES blockchain_network_participants(participant_id),
    permission_id INT REFERENCES access_permissions(permission_id),
    PRIMARY KEY (participant_id, permission_id)
);

CREATE TABLE blockchain_user_permissions (
   user_id INT PRIMARY KEY,
   user_name VARCHAR(255) NOT NULL,
   access_level ENUM('admin', 'read', 'write') NOT NULL
);

CREATE TABLE blockchain_role_permissions (
   role_id INT PRIMARY KEY,
   role_name VARCHAR(255) NOT NULL
);

CREATE TABLE blockchain_network_permissions (
   network_id INT PRIMARY KEY,
   network_name VARCHAR(255) NOT NULL,
   permissions TEXT
);

CREATE TABLE blockchain_user_roles (
   user_id INT,
   role_id INT,
   FOREIGN KEY (user_id) REFERENCES blockchain_user_permissions(user_id),
   FOREIGN KEY (role_id) REFERENCES blockchain_role_permissions(role_id)
);

CREATE TABLE blockchain_role_network (
   role_id INT,
   network_id INT,
   FOREIGN KEY (role_id) REFERENCES blockchain_role_permissions(role_id),
   FOREIGN KEY (network_id) REFERENCES blockchain_network_permissions(network_id)
);
