);

CREATE TABLE identity_documents (
    doc_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    doc_type VARCHAR(50) NOT NULL,
    doc_number VARCHAR(20) NOT NULL
);

CREATE TABLE blockchain_participants (
    participant_id INT PRIMARY KEY,
