tables.table_schema:
CREATE TABLE decision_audit_trail (
    id INT PRIMARY KEY,
    decision_text TEXT,
    decision_maker TEXT,
    decision_date DATE
);

CREATE TABLE action_audit_trail (
    id INT PRIMARY KEY,
    action_description TEXT,
    action_taker TEXT,
    action_date DATE
);

CREATE TABLE decision_action_link (
    id INT PRIMARY KEY,
    decision_id INT,
    action_id INT
);

CREATE TABLE governance_decisions (
   decision_id UUID PRIMARY KEY,
   decision_type VARCHAR(50),
   decision_date DATE,
   decision_description TEXT
);

CREATE TABLE governance_actions (
   action_id UUID PRIMARY KEY,
   action_type VARCHAR(50),
   action_date DATE,
   action_description TEXT
);

CREATE TABLE decision_makers (
   maker_id SERIAL PRIMARY KEY,
   maker_name VARCHAR(100),
   maker_role VARCHAR(50)
);

CREATE TABLE action_participants (
   participant_id SERIAL PRIMARY KEY,
   participant_name VARCHAR(100),
   participant_role VARCHAR(50)
);
