tables.table_schema:
CREATE TABLE suspicious_transactions (
    transaction_id UUID PRIMARY KEY,
    sender_address TEXT,
    receiver_address TEXT,
    amount DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE transaction_metadata (
    metadata_id UUID PRIMARY KEY,
    transaction_id UUID REFERENCES suspicious_transactions(transaction_id),
    metadata TEXT
);

CREATE TABLE flagged_addresses (
  address_id UUID PRIMARY KEY,
  address VARCHAR(255) UNIQUE NOT NULL,
  flagged_reason VARCHAR(255) NOT NULL
);

CREATE TABLE transaction_logs (
  log_id UUID PRIMARY KEY,
  transaction_id UUID REFERENCES suspicious_transactions (transaction_id),
  log_message TEXT NOT NULL,
  log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE blocks (
    block_id INT PRIMARY KEY,
    block_hash VARCHAR(255),
    previous_block_hash VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE addresses (
    address_id INT PRIMARY KEY,
    address VARCHAR(255),
    is_suspicious BOOLEAN
);

CREATE TABLE suspicious_accounts (
    account_id INT PRIMARY KEY,
    account_address VARCHAR(255),
    account_type VARCHAR(100),
    suspicious_level INT
);

CREATE TABLE blockchain (
    block_id INT PRIMARY KEY,
    block_hash VARCHAR(255),
    previous_block_hash VARCHAR(255),
    block_timestamp TIMESTAMP
);

CREATE TABLE suspect_transactions (
    transaction_id INT PRIMARY KEY,
    suspect_reason TEXT,
    suspect_score INT
);

CREATE TABLE transaction_details (
    transaction_id INT PRIMARY KEY,
    transaction_hash VARCHAR(255),
    block_id INT,
    confirmation_status BOOLEAN
);
