tables.table_schema:
CREATE TABLE monitoring_logs (
  log_id SERIAL PRIMARY KEY,
  node_id INT REFERENCES blockchain_node_metadata(node_id),
  log_timestamp TIMESTAMP NOT NULL,
  log_message TEXT
);

CREATE TABLE blockchain_nodes (
  node_id INT PRIMARY KEY,
  node_name VARCHAR(255),
  node_address VARCHAR(255)
);

CREATE TABLE block_metadata (
  block_id INT PRIMARY KEY,
  block_hash VARCHAR(255),
  block_timestamp TIMESTAMP
);

CREATE TABLE node_connections (
  connection_id INT PRIMARY KEY,
  node_id INT,
  connected_node_id INT,
  connection_timestamp TIMESTAMP
);

CREATE TABLE blocks (
    block_id UUID PRIMARY KEY,
    previous_block_id UUID,
    miner_address VARCHAR(50),
    timestamp TIMESTAMPTZ
);

CREATE TABLE block_transactions (
    block_id UUID,
    transaction_id UUID,
    FOREIGN KEY (block_id) REFERENCES blocks(block_id),
    FOREIGN KEY (transaction_id) REFERENCES transactions(transaction_id)
);

CREATE TABLE blockchain_nodes (
    id INT PRIMARY KEY,
    node_id UUID,
    node_url TEXT,
    node_status BOOLEAN
);

CREATE TABLE block_headers (
    block_hash UUID PRIMARY KEY,
    block_number INT,
    timestamp TIMESTAMPTZ,
    node_id UUID
);

CREATE TABLE node_logs (
    log_id INT PRIMARY KEY,
    node_id UUID,
    log_timestamp TIMESTAMPTZ,
    message TEXT
);
