tables.table_schema:
CREATE TABLE blockchain_transactions (
    transaction_id SERIAL PRIMARY KEY,
    transaction_hash VARCHAR(100) NOT NULL,
    digital_signature BYTEA NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE signature_keys (
    key_id SERIAL PRIMARY KEY,
    public_key BYTEA NOT NULL,
    private_key BYTEA NOT NULL
);

CREATE TABLE blockchain_transactions (
    transaction_id UUID PRIMARY KEY,
    transaction_data JSONB,
    digital_signature BYTEA
);

CREATE TABLE signature_metadata (
    signature_id SERIAL PRIMARY KEY,
    transaction_id UUID,
    signer_name VARCHAR(255),
    signing_timestamp TIMESTAMP
);

CREATE TABLE transaction_history (
    history_id SERIAL PRIMARY KEY,
    transaction_id UUID,
    transaction_status TEXT,
    transaction_timestamp TIMESTAMP
);

CREATE TABLE signatures (
  id SERIAL PRIMARY KEY,
  transaction_id UUID NOT NULL,
  signature TEXT NOT NULL
);

CREATE TABLE transactions (
  id SERIAL PRIMARY KEY,
  hash BYTEA NOT NULL,
  amount DECIMAL(18,8) NOT NULL
);

CREATE TABLE users (
  id SERIAL PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  public_key TEXT NOT NULL
);

CREATE TABLE blocks (
  id SERIAL PRIMARY KEY,
  block_hash BYTEA NOT NULL,
  timestamp TIMESTAMPTZ NOT NULL
);

CREATE TABLE digital_signatures (
    id UUID PRIMARY KEY,
    transaction_id UUID,
    signature BYTEA
);

CREATE TABLE blockchain_transactions (
    transaction_id UUID PRIMARY KEY,
    block_number INT,
    timestamp TIMESTAMP,
    sender_address VARCHAR(255),
    receiver_address VARCHAR(255),
    amount DECIMAL(18, 2)
);

CREATE TABLE blockchain_blocks (
    block_number INT PRIMARY KEY,
    previous_block_number INT,
    timestamp TIMESTAMP,
    miner_address VARCHAR(255)
);

CREATE TABLE miners (
    miner_id UUID PRIMARY KEY,
    miner_name VARCHAR(255),
    mining_power DOUBLE PRECISION
);

CREATE TABLE mining_rewards (
    reward_id UUID PRIMARY KEY,
    miner_id UUID,
    block_number INT,
    reward_amount DECIMAL(18, 2)
);
