CREATE TABLE blockchain_network_changes (
    upgrade_id SERIAL PRIMARY KEY,
    upgrade_name TEXT NOT NULL,
    upgrade_description TEXT,
    upgrade_date TIMESTAMP NOT NULL
);

CREATE TABLE blockchain_network_upgrades (
  upgrade_id UUID PRIMARY KEY,
  upgrade_name VARCHAR(255),
  upgrade_date TIMESTAMP,
  upgrade_description TEXT
);

CREATE TABLE blockchain_upgrades (
  upgrade_id SERIAL PRIMARY KEY,
  upgrade_name VARCHAR(100) NOT NULL,
  upgrade_date DATE NOT NULL,
  upgrade_description TEXT
);

CREATE TABLE blockchain_upgrades (
    upgrade_id UUID PRIMARY KEY,
    upgrade_name VARCHAR(255),
    upgrade_description TEXT,
    upgrade_date TIMESTAMP
);

CREATE TABLE network_changes (
    change_id UUID PRIMARY KEY,
    change_description TEXT,
    affected_blockchain VARCHAR(255),
    change_date TIMESTAMP
);

CREATE TABLE upgrade_details (
    detail_id UUID PRIMARY KEY,
    upgrade_id UUID,
    detail_description TEXT,
    FOREIGN KEY (upgrade_id) REFERENCES blockchain_upgrades(upgrade_id)
);

CREATE TABLE blockchain_network_updates (
  id SERIAL PRIMARY KEY,
  upgrade_name VARCHAR(50) NOT NULL,
  upgrade_description TEXT,
  upgrade_date DATE
);

CREATE TABLE blockchain_changes (
  id SERIAL PRIMARY KEY,
  change_id INT NOT NULL,
  change_description TEXT,
  change_date DATE
);

CREATE TABLE blockchain_version (
  id SERIAL PRIMARY KEY,
  version_number DECIMAL(5,2) NOT NULL,
  version_description TEXT,
  released_date DATE
);

CREATE TABLE blockchain_transactions (
  id SERIAL PRIMARY KEY,
  transaction_id INT NOT NULL,
  transaction_type VARCHAR(20) NOT NULL,
  transaction_date DATE
);

CREATE TABLE blockchain_blocks (
  id SERIAL PRIMARY KEY,
  block_id INT NOT NULL,
  block_data TEXT,
  block_timestamp TIMESTAMP
);
