tables.table_schema:
CREATE TABLE models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(50),
    model_file BLOB,
    created_at TIMESTAMP
);

CREATE TABLE textures (
    texture_id INT PRIMARY KEY,
    texture_name VARCHAR(50),
    texture_file BLOB,
    created_at TIMESTAMP
);

CREATE TABLE models (
  model_id INT PRIMARY KEY,
  model_name VARCHAR(255) NOT NULL,
  model_description TEXT
);

CREATE TABLE textures (
  texture_id INT PRIMARY KEY,
  texture_name VARCHAR(255) NOT NULL,
  texture_file BLOB
);

CREATE TABLE simulations (
  simulation_id INT PRIMARY KEY,
  simulation_name VARCHAR(255) NOT NULL,
  model_id INT REFERENCES models(model_id),
  texture_id INT REFERENCES textures(texture_id)
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username TEXT NOT NULL UNIQUE,
    email TEXT UNIQUE,
    password TEXT NOT NULL
);

CREATE TABLE Models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255),
    model_description TEXT,
    model_file_path VARCHAR(255)
);

CREATE TABLE Textures (
    texture_id INT PRIMARY KEY,
    texture_name VARCHAR(255),
    texture_file_path VARCHAR(255)
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255),
    user_email VARCHAR(255)
);

CREATE TABLE UserPermissions (
    permission_id INT PRIMARY KEY,
    user_id INT,
    role VARCHAR(50),
    FOREIGN KEY (user_id) REFERENCES Users(user_id)
);
