tables.table_schema:
CREATE TABLE geolocation_data (
    location_id SERIAL PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    challenge_id INT
);

CREATE TABLE challenge_data (
    challenge_id SERIAL PRIMARY KEY,
    challenge_name VARCHAR(255),
    location_id INT
);

CREATE TABLE Challenges (
    challenge_id SERIAL PRIMARY KEY,
    challenge_name VARCHAR(50) NOT NULL,
    location_lat DECIMAL(9,6) NOT NULL,
    location_long DECIMAL(9,6) NOT NULL
);

CREATE TABLE Participants (
    participant_id SERIAL PRIMARY KEY,
    participant_name VARCHAR(50) NOT NULL,
    challenge_id INT REFERENCES Challenges(challenge_id)
);

CREATE TABLE Clues (
    clue_id SERIAL PRIMARY KEY,
    clue_text TEXT NOT NULL,
    challenge_id INT REFERENCES Challenges(challenge_id)
);

CREATE TABLE challenge_locations (
    location_id UUID PRIMARY KEY,
    location_name VARCHAR(50),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE challenges (
    challenge_id UUID PRIMARY KEY,
    challenge_name VARCHAR(50),
    location_id UUID REFERENCES challenge_locations(location_id),
    difficulty VARCHAR(20)
);

CREATE TABLE players (
    player_id UUID PRIMARY KEY,
    player_name VARCHAR(50),
    email VARCHAR(50)
);

CREATE TABLE player_progress (
    player_id UUID REFERENCES players(player_id),
    challenge_id UUID REFERENCES challenges(challenge_id),
    completion_status BOOLEAN,
    points INTEGER
);
