tables.table_schema:
CREATE TABLE in_app_purchases (
    purchase_id INT PRIMARY KEY,
    item_name VARCHAR(50) NOT NULL,
    price DECIMAL(10, 2) NOT NULL,
    purchase_date TIMESTAMP NOT NULL
);

CREATE TABLE virtual_currency (
    user_id INT PRIMARY KEY,
    currency_balance INT DEFAULT 0
);

CREATE TABLE app_users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL,
    created_at TIMESTAMP
);

CREATE TABLE purchases (
    purchase_id SERIAL PRIMARY KEY,
    user_id UUID REFERENCES app_users(user_id),
    purchase_amount DECIMAL NOT NULL,
    purchase_date TIMESTAMP
);

CREATE TABLE virtual_currency (
    currency_id SERIAL PRIMARY KEY,
    currency_name VARCHAR(50) NOT NULL,
    exchange_rate DECIMAL NOT NULL
);

CREATE TABLE user_wallets (
    wallet_id SERIAL PRIMARY KEY,
    user_id UUID REFERENCES app_users(user_id),
    currency_id INT REFERENCES virtual_currency(currency_id),
    balance DECIMAL NOT NULL
);
